pragma solidity ^0.6.1;
pragma experimental "ABIEncoderV2";

contract FuriousManagingOtter_v1  {

    //Constants
    uint constant POOL_SIZE = 30;
    uint constant TIMEOUT_INTERVAL = 60 * 60;   //May not lead to more than 256 / 3 blocks mined within an interval. If we want to allow more than, we will have to replace checkpointNumber in the contract information with a checkpoint hash and refer to the previous block
                                                //The executive operator can get "extra time" by first challenging the watchdogs

    //For attestation
    bytes32 constant WRAPPER_PROGRAM_CODE_HASH = 0; //TODO @Patrick: Insert Codehash of the wrapper program here
    bytes constant ATTESTATION_KEY = "\x00";        //TODO @Patrick: Insert public key of the attestation authority here

    //Phases a contract can occupy
    enum Phase { NONE, CREATION, EXECUTING, CHALLANGE_EXECUTOR, CHALLENGE_WATCHDOG, CRASHED }

    //Events 
    event NewOperator(address operator);
    event CreationInitialized(uint id);
    event ContractCreated(uint id);
    event ExecutorChallenged(uint id, address executor, bytes message);
    event ExecutorResponse(uint id, address executor, bytes response);
    event ExecutorKicked(uint id, address oldExecutor, address newExecutor);
    event WatchdogChallenged(uint id, bool[POOL_SIZE] dogs, bytes message);
    event WatchdogResponse(uint id, address dog, bytes response);
    event WatchdogFinalized(uint id);
    event ContractCrashed(uint id);

    //Operator information 
    struct Operator {
        bool initialized;
        address teeSignatureAddress;
        bytes teeEncryptionKey;
    }

    //Contract information
    struct Contract {

        //General lifecycle management
        Phase phase;                                //Current phase
        uint checkpointNumber;                      //Number of the last known blockchain interaction

        //Contract and pool information
        address payable poolAddress;                // The address to the pool public key
        address creationOperator;                   // The address of the creation operator
        address[POOL_SIZE] operators;               // The operator pool, a kicked operator is one witha ddress(0)
        uint executiveOperator;                     // The index of the current executive operator within the operator pool
        bytes32 codeHash;                           // The code hash of the application logic

        //Dispute information
        Phase fallbackPhase;                        // A watchdog challenge can be within another challange. Therefore we need to store the fallback phase after the watchdog challange
        bool watchdogsChallanged;                   // Watchdogs may only be challanged once during an execution challange. This variable helprs to ensure that
        bytes32 execChallangeHash;                  // The hash of the challange message of the executor challange
        bytes32 watchdogChallangeHash;              // The hash of the challangeHash of the watchdog challange (we need both as watchdog challange can be within executor challange)
        bool[POOL_SIZE] challangedWatchdogs;        // Array that stores which watchdogs are challanged
        uint deadline;
    }

    //Operator mapping
    mapping(address => Operator) public operators;
    address[] public operatorList;                  //To enable the client to iterate over all operators, if we wanted to save gas, we could also iterate over the past events

    //Contract mapping
    mapping(uint => Contract) public contracts;


    //******************
    //Public functions


    //******************
    //Registration

    //Registers a Operator and attests TEE, there may only be one TEE per operator
    function register(address teeSignatureAddress, bytes memory teeEncryptionKey, bytes memory attestationSignature) public {

        //Check that operator has not registered another TEE
        require(operators[msg.sender].initialized == false, "The operator has already registered a TEE!");

        //TODO: Attest the TEE
        //Check the signature with the required signature algorithm //TODO @DKoisser: Here you can see what needs to be included into the attestation of a TEE
        bytes32 attestationHash = keccak256(abi.encode("Furious-Attest", WRAPPER_PROGRAM_CODE_HASH, teeSignatureAddress, teeEncryptionKey));
        require(verifyAttestationSignature(attestationHash, attestationSignature, ATTESTATION_KEY), "Wrong attestation");

        //Register operator
        operatorList.push(msg.sender);
        operators[msg.sender] = Operator(true, teeSignatureAddress, teeEncryptionKey);

        //Notify clients
        emit NewOperator(msg.sender);
    }

    //******************
    //Creation

    //We do not challange the creator. If he does not respond in time, the creation is simply not successful. Challange would have the same effect.

    //Initializes the creation, we need this to ensure that the creation TEE gets the up-to-date state (e.g. list of operators) from the operator
    function initCreation(address creationOperator, bytes32 codeHash, uint freeId) public {

        //Checks
        require(contracts[freeId].phase == Phase.NONE, "Contract with this ID does already exist!");
        require(operators[creationOperator].initialized == true, "Creation operator does not exist!");

        //Creat contract Tuple
        address[POOL_SIZE] memory operatorArray;
        bool[POOL_SIZE] memory challanged;
        contracts[freeId] = Contract(Phase.CREATION, block.number, address(0), creationOperator, operatorArray, 0, codeHash, Phase.NONE, false, 0, 0, challanged, now + TIMEOUT_INTERVAL);

        //Notify clients
        emit CreationInitialized(freeId);
    }

    //Enables the creation operator to challange the pool during creation
    //Problem: During creation the TEEs do not receive the same message as they do not know the secret pool key
    //Solution: Simply interpret message as the concatination of all individual messages
    function challangeWatchdogsDuringCreation(uint id, address[POOL_SIZE] memory pool_operators, bytes memory message, bool[POOL_SIZE] memory challangedOperators) public {

        //Checks
        require(msg.sender == contracts[id].creationOperator, "Not the right account to challenge the watchdogs");                                                                                            
        require(contracts[id].phase == Phase.CREATION && contracts[id].watchdogsChallanged == false, "Not the right phase to challange watchdogs or watchdogs have already been challanged during creation!");

        //I think we can set this without a signature as it will be overwritten by a successful creation anyways
        contracts[id].operators = pool_operators;

        //Call the internal part of the watchdog challange procedure (shared between watchdog challanges during execution and during creation)
        internalWatchdogChallange(id, message, challangedOperators);
    }

    //Finalizes the creation. The TEE ensures that the values (e.g. Operator pool) are correct. We only need to check via the TEE-signature that it has received the correct inputs
    function finalizeCreation(uint id, address payable pool_address, address[POOL_SIZE] memory pool_operators, bytes memory signature) public {

        //Checks
        require(contracts[id].phase == Phase.CREATION, "The contract with this ID is not in the right phase to be created!");
        require(contracts[id].deadline <= now, "The creation deadline has expired!");

        //Check Signature //TODO @DKoisser: Here you can see what needs to be included into the creation finalization signature
        bytes32 signedHash = keccak256(abi.encode("Creation-Attest", id, blockhash(contracts[id].checkpointNumber), pool_address, contracts[id].codeHash, pool_operators));
        require(verifySignature(signedHash, signature, operators[contracts[id].creationOperator].teeSignatureAddress) == true, "Wrong signature for creation!");

        //Update state
        contracts[id].poolAddress = pool_address;
        contracts[id].phase = Phase.EXECUTING;
        contracts[id].operators = pool_operators;

        //Notify clients
        emit ContractCreated(id);
    }

    //******************
    //Handling assets

    //Enables a party to deposit funds to a pool address, as we increment the checkpoint number this may only be done during phase executing
    function depositToContract(uint id) public payable{

        //You can only deposit to an executing contract
        require(contracts[id].phase == Phase.EXECUTING, "Contract with this ID is not in phase EXECUTING!");

        //Update checkpoint = block number of last interaction
        contracts[id].checkpointNumber = block.number;

        //Forward the money to the pool address
        contracts[id].poolAddress.transfer(msg.value);
    }

    //Enables a party to withdraw funds. The tx needs to be issued by the poolAddress (the pool-pk) and is broadcasted to the network by the client.
    //The tx contains the block number and block hash, when it was issued.
    //This way we ensure that the pool has been up-to-date with the blockchain when it has issued this payout. 
    function withdraw(uint id, uint blocknumber, bytes32 claimedBlockash, address payable receiver) public payable {

        //Checks
        require(msg.sender == contracts[id].poolAddress, "The sender of the payout tx is not the pool with the specified ID!");
        require(blockhash(blocknumber) == claimedBlockash, "Wrong blockhash for the given block number!");

        //Forwards the withdraw value to the receiver of the payout
        receiver.transfer(msg.value);
    }

    //******************
    //Execution Challenges - Can either be atomic (just challange the executor) or combined with a challange to the watchdogs by the executor. Needs to be complete before completing the challange to the executor

    //Challenge the executor to provide the current state or execute a move (what exactly is encoded into the message)
    function challengeExecutor(uint id, bytes memory message) public {

        //Checks
        require(contracts[id].phase == Phase.EXECUTING, "Can only challenge executor of contract in phase executing!");

        //Update state
        contracts[id].phase == Phase.CHALLANGE_EXECUTOR;
        contracts[id].deadline == now + TIMEOUT_INTERVAL;
        contracts[id].watchdogsChallanged = false;
        contracts[id].checkpointNumber = block.number;
        contracts[id].execChallangeHash = keccak256(message);

        //Notify clients
        emit ExecutorChallenged(id, contracts[id].operators[contracts[id].executiveOperator], message);

    }

    //Response of the executive operator
    function executorResponse(uint id, bytes memory response, bytes memory signature) public {

        //Checks
        require(contracts[id].phase == Phase.CHALLANGE_EXECUTOR, "Challenge can only be answered if there is an unresolved challenge!");
        require(contracts[id].deadline <= now, "The response deadline has expired!");

        //Check Signature //TODO @DKoisser: Here you can see what needs to be included into the executor response signature
        bytes32 signedHash = keccak256(abi.encode("Challenge-Response", id, blockhash(contracts[id].checkpointNumber), response));  //Checkpoint-hash includes contracts[id].challangeHash
        require(verifySignature(signedHash, signature, operators[contracts[id].operators[contracts[id].executiveOperator]].teeSignatureAddress) == true, "Wrong signature for executor response!");

        //Update state
        contracts[id].phase == Phase.EXECUTING;
        contracts[id].checkpointNumber = block.number;

        //Notify clients
        emit ExecutorResponse(id, contracts[id].operators[contracts[id].executiveOperator], response);
    }

    //Enables any party to kick the executive operator if it does not respond to a challange in time
    function executorTimeout(uint id) public {

        //Checks
        require(contracts[id].phase == Phase.CHALLANGE_EXECUTOR, "Challenge can only be timed out if there is an unresolved challenge!");
        require(contracts[id].deadline > now, "The response deadline has not expired!");

        //Drop the executor = Set his address to address(0)
        address _executor = contracts[id].operators[contracts[id].executiveOperator];
        contracts[id].operators[contracts[id].executiveOperator] = address(0);

        //Check if there is another active operator
        for(uint i = contracts[id].executiveOperator + 1; i < POOL_SIZE; i++) {
            if(contracts[id].operators[i] != address(0)){

                //Update state
                contracts[id].executiveOperator = i;
                contracts[id].checkpointNumber = block.number;
                contracts[id].phase == Phase.EXECUTING;

                //Notify clients
                emit ExecutorKicked(id, _executor, contracts[id].operators[contracts[id].executiveOperator]);

                return;
            }
        }

        //Otherwise: Mark the contract as crashed
        contracts[id].phase == Phase.CRASHED;
        contracts[id].checkpointNumber = block.number;

        //Notify clients
        emit ContractCrashed(id);

    }

    //********************
    // Challenge watchdogs

    //Enables the executors to challenge a list of watchdogs with a given message.
    //If their are individual messages for each watchdog, simply concatenate them to one 
    function challengeWatchdog(uint id, bytes memory message, bool[POOL_SIZE] memory challangedOperators) public {

        //Checks
        require(msg.sender == contracts[id].operators[contracts[id].executiveOperator], "Not the right account to challenge the watchdogs");                                                                                            
        require(contracts[id].phase == Phase.EXECUTING || (contracts[id].phase == Phase.CHALLANGE_EXECUTOR && contracts[id].watchdogsChallanged == false), "Not the right phase to challange watchdogs or watchdogs have already been challanged within the same executor challange!");   //Correct phase

        //Internal function to prevent duplicate code
        internalWatchdogChallange(id, message, challangedOperators);

    }

    //Answers a watchdog challenge. The operator gives the challenge to the TEE which generates a response. The response can also be: <<Invalid Message>>
    //Id 1 is the index of the party in the operator-list, Id 2 is the index of the party in the challanged-list
    function watchdogResponse(uint id, uint index, bytes memory response, bytes memory signature) public {

        //Checks
        require(contracts[id].phase == Phase.CHALLENGE_WATCHDOG, "Not the right phase to provude a watchdog response");
        require(contracts[id].deadline <= now, "The response deadline has expired!");

        //Verify signatures //TODO @DKoisser: Here you can see what needs to be included into a watchdog response signature
        bytes32 signedHash = keccak256(abi.encode("Watchdog-challenge-Response", id, blockhash(contracts[id].checkpointNumber), response));  //Checkpoint-hash includes contracts[id].challangeHash
        require(verifySignature(signedHash, signature, operators[contracts[id].operators[index]].teeSignatureAddress) == true, "Wrong signature for executor response!");

        //Mark the party as not-challanged in the challange list
        contracts[id].challangedWatchdogs[index] = false;

        //Notify clients
        emit WatchdogResponse(id, contracts[id].operators[index], response);
    }

    //Finalizes a watchdog challange and kicks all watchdogs that have not responded
    function watchdogFinalization(uint id) public {

        //Checks
        require(contracts[id].phase == Phase.CHALLENGE_WATCHDOG, "Not the right phase to provude a watchdog response");
        require(contracts[id].deadline > now, "The response deadline has not expired!");

        //Drop the watchdogs, that have not responded (The executor is ignored here)
        for(uint i = 0; i < POOL_SIZE; i++){
            if(contracts[id].challangedWatchdogs[i] == true){
                contracts[id].operators[i] == address(0);       //A kicked watchdog is simply one with address(0)
            }
        }

        //Return to the correct phase
        contracts[id].phase = contracts[id].fallbackPhase;
        contracts[id].watchdogsChallanged = true;

        //Increase the timeout if there is an outer creation or execturo challange
        if (contracts[id].phase == Phase.CHALLANGE_EXECUTOR || contracts[id].phase == Phase.CREATION) {
            contracts[id].deadline == now + TIMEOUT_INTERVAL;
        }

        //Increment the block count
        contracts[id].checkpointNumber = block.number;

        //Notify clients
        emit WatchdogFinalized(id);
    }

    //******************
    //Private functions (Mainly prevent duplicate code)

    //Duplicate code used by both watchdog challanges which dupdates the state and emits the event
    function internalWatchdogChallange(uint id, bytes memory message, bool[POOL_SIZE] memory challangedOperators) private {

        //Update state
        contracts[id].fallbackPhase = contracts[id].phase;
        contracts[id].phase == Phase.CHALLENGE_WATCHDOG;
        contracts[id].deadline == now + TIMEOUT_INTERVAL;
        contracts[id].checkpointNumber = block.number;
        contracts[id].watchdogChallangeHash = keccak256(message);
        contracts[id].challangedWatchdogs = challangedOperators;

        //Notify clients
        emit WatchdogChallenged(id, challangedOperators, message);
    }


    //******************
    //Public helper functions

    //Verifies ECDSA signatures of Ethereum addresses
    function verifySignature(bytes32 signedHash, bytes memory signature, address signer) public pure returns (bool) {

        //Parse the signature
        (uint8 v, bytes32 r, bytes32 s) = abi.decode(signature, (uint8, bytes32, bytes32));

        //Verify the signature
        return (ecrecover(signedHash, v, r, s) == signer);

    }

    //Verifies (TODO @Patrick: What kind of signatures) signatures of public keys (bytes array) for attestation
    function verifyAttestationSignature(bytes32 signedHash, bytes memory signature, bytes memory attestationKey) public pure returns (bool) {

        //Parse the signature and the key
        //TODO @Patrick

        //Check signature and return if it is correct
        //TODO @Patrick
        //@Patrick: When you know which signature algorithm will be used, you can also ask Benni to ask our HiWi for a signature verification for that algorithm.

        return true;
    }
}
